               if (ri.startsWith("乙")){
                    if (shi.equals("丙子")){string+="乙木生 子月 丙子 時 仲冬嚴寒凛冽，乙木枝葉皆悴，八字非有陽和解凍，木無生氣，乙日子提，坐下水印，時支又見子水，生而反尅之象，時干丙火，陽氣虛脫，不足以除寒，身弱獨喜寅卯等字，火土亦為喜見之神。 ";}
                    if (shi.equals("丁丑")){string+="乙木生 子月 丁丑 時 乙誕子月，木根盡濕，時丁坐丑，火氣盡泄，子丑氣秉北方，旺水有凍木之憂，以時上一丁之燄，而欲解嚴寒之凍，力有未逮，最喜甲透天干以引丁，其用等於丙火，更有亢燥之土以制水，則去病殆盡矣。 ";}
                    if (shi.equals("戊寅")){string+="乙木生 子月 戊寅 時 日元乙木，通根於時支之寅，蓋寅中丙戊得生，氣勢極旺，時干戊財，足以收提防之功，所喜月提子水，藏而不透，無損木之精神，如能加以木比火傷，木更向榮欣欣矣。 ";}
                    if (shi.equals("已卯")){string+="乙木生 子月 已卯 時 日元乙木，得祿於時支之卯，格成歸祿，子卯雖刑，動而不動，仍可全其水木之氣，時干一已，以坐下卯地而力薄，喜有火之食傷，雖重重不厭其多，冬乙以丙為真神，正如赤子慈母之不可或離也。";}
                    if (shi.equals("庚辰")){string+="乙木生 子月 庚辰 時 仲冬乙木，月子時辰，會成半水之局，八字時干庚金，雖有辰土之生，終亦助水之旺，水印獨多，母旺子衰，過於清寒，喜有土以制水，火以暖木。";}
                    if (shi.equals("辛巳")){string+="乙木生 子月 辛巳 時 乙誕子月，木性寒而且濕，時支坐下巳火，可以驅寒轉暖，時干辛殺，以臨巳火之位，不能傷我日元，冬水反生為尅，故不欲水盛，非僅漂木，抑且去火之燄，則木不能賴火以發榮矣。";}
                    if (shi.equals("壬午")){string+="乙木生 子月 壬午 時 冬月乙木，氣寒而冽，月提子水，時支午火，子午一冲，則水愈旺而火愈弱，書云，衰者冲旺旺者發，旺者冲衰衰者拔，今子旺午衰，午火被拔，時透一壬，水勢益增，乙木更形漂蕩矣，大喜厚土旺火，精神方可發越。 ";}
                    if (shi.equals("癸未")){string+="乙木生 子月 癸未 時 乙日子提，癸水透於時干，似有寒木之意，不知癸水坐下未土，適得制水之功，未中暗藏丁火，木氣得以溫暖，惟以丁藏力弱，須有甲木引丁之燄方妙，金乃助水之神，固無禆於冬木也。 ";}
                    if (shi.equals("甲申")){string+="乙木生 子月 甲申 時 日時甲乙並列，虛露不着根底，月子時申，會成半水之局，印綬結合，似有益於木，不知仲冬木氣畏寒，水旺足以損木，八字再見透干之水，自不能無厚土制之，木寒喜神在火，有一丙透，木性遂臻陽和矣。 ";}
                    if (shi.equals("乙酉")){string+="乙木生 子月 乙酉 時 丙乙並露干頭，月子時酉相生，水旺而復有金生，有根之木，尚患寒凍，况木虛而不載於地乎，然則須有土以培木去水，更喜有火溫暄，多火則木自向榮。 ";}
                    if (shi.equals("丙戌")){string+="乙木生 子月 丙戌 時 日元乙木，誕於仲冬時節，木氣盤屈於地，其象頗不舒展，時干丙火高透，通根於戌，傷財兩全其真，惟日元孤虛，必須有刦比之扶助，始臻完備，如畫龍雖好，尚有賴於點睛也。 ";}
                    if (shi.equals("丁亥")){string+="乙木生 子月 丁亥 時 乙木日元，時支坐下長生，本身得地有根，亥子皆屬北方，經此旺水浸淫，寒木反生為尅，時干一丁坐於水地，星星之火，力嫌太微，所喜重火以暄之，厚土以實之，則無情轉為有情矣。";}
               }
               if (ri.startsWith("丙")){

                    if (shi.equals("戊子")){string+="丙火生 子月 戊子 時 仲冬丙火，一陽潛生，火力勢甚微弱，月時兩坐子位，有躡丙火之氣，八字時干戊土，有晦丙火之光，身主孤立無助，宜以木印泄水生火，兼有巳午等字為佳。 ";}
                    if (shi.equals("已丑")){string+="丙火生 子月 已丑 時 子丑氣歸北方，兼有丑中辛癸之助，時干已土，幾以水旺而成為濕泥，日主丙火，孱弱極矣，一點陽威，泄之殆盡，書云，冬火欲生不欲殺，殺則歇滅，然則，欲其恢復通明之象，非木印重見，或火比得地，不可。 ";}
                    if (shi.equals("庚寅")){string+="丙火生 子月 庚寅 時 丙火長生於寅，寅中印比兩生，冬月火勢絶滅，有一寅助，氣轉生旺，月提子水，雖寒，藏支原無大礙，時庚雖能生水，幸坐下寅木絶地，生而不生，如干透壬癸等字，用土制之，格成食傷制殺。 ";}
                    if (shi.equals("辛卯")){string+="丙火生 子月 辛卯 時 丙火日元，誕於寒冬，體絶忘形，月時子卯一刑，似乎卯印不能生身，但滴天髓云，柱神祇以冲為重，刑與穿兮動不動，尚無大礙，可以明矣，時干辛金成凍，且惡其無情合丙，除非化水，否則須多火來心助。 ";}
                    if (shi.equals("壬辰")){string+="丙火生 子月 壬辰 時 子辰半會水局，時干再透壬水，其勢已成氾濫，日元丙火，孤立無依，冬火不見印比生扶，如頻見水金同來者，可作從殺論，一見木火得地，從格破矣，仍喜生扶。 ";}
                    if (shi.equals("癸巳")){string+="丙火生 子月 癸巳 時 丙火得祿於巳，癸水坐祿於子，身與官，交互得祿，惟此時水當令而火退氣，八字苟有木印以助身之旺，構通水火坎離之氣，再見金財生官，則成點睛之畫龍矣。 ";}
                    if (shi.equals("甲午")){string+="丙火生 子月 甲午 時 丙火見午為刃，冬火見刃為美，時干一甲，又來幫身，身主得氣，惟月令子水乘旺，見午則冲，旺者冲衰，衰者難存，書云，敗地逢冲仔細推，敗地，指子午卯酉也，着重仔細兩字，今以羊刃被損，日元旺又轉弱，仍喜有得地木火以補救之，否則，須有戌未制火，以解子午之冲。 ";}
                    if (shi.equals("乙未")){string+="丙火生 子月 乙未 時 時逢乙未，印綬歸庫，未中一點丁火，又來生助丙火，仲冬一陽復生，丙乃弱轉為强，祇要印比得地，更不為弱，月提子水，遇未而被傷，未可引以為用，須有金水透干，方取財生官殺。 ";}
                    if (shi.equals("丙申")){string+="丙火生 子月 丙申 時 天干兩丙並列，地支子申會水，天干不載於地，而地支反來尅丙，上下無情，大凡水火交戰，最喜有通關之神，而弱火更不可少木印以助之，由是火水之氣和，而弱極之身得生矣。";}
                    if (shi.equals("丁酉")){string+="丙火生 子月 丁酉 時 丙丁列午，刧比本屬同氣，酉子坐支，金水情誼相投，水虛水實，乃是官旺身輕，值茲嚴冬之際，水勢正旺，故八字忌水再透干肆逞，須要丙火得地，兼有木印為助，然後再用官殺，凡論一造，衰旺之真機，總不可不顧及也。 ";}
                    if (shi.equals("戊戌")){string+="丙火生 子月 戊戌 時 丙火坐庫於戌，身主尚云有根，時上戊戌，食重可作傷看，且其病在晦火，月令子水，官星已被土之包圍，無存在可能，土重洩身，喜有刼比補之，重木以去重土，方收有病得藥之效。 ";}
                    if (shi.equals("已亥")){string+="丙火生 子月 已亥 時 亥子氣全北方，秉令之水，成方而愈旺，日元丙火，全恃亥中甲印，乃以帶水之木，究未能發丙之燄，是水火未濟之象也，身弱以幫身為先，故支見寅巳卯午等為宜，如重見金水同來，則當順其崑崙之勢而從殺。 ";}
               }
                if (ri.startsWith("丁")){

                    if (shi.equals("庚子")){string+=" 丁火生 子月 庚子 時 丁誕子月，嚴寒凛冽，水歸冬旺，有困丁火，月時兩子，七殺勢專，一庚透時，徒增水之凍，日元丁火，幾被旺水熄滅，是其八字病則在水矣。去病之法，莫如用木泄水之氣，用帶火之土以塞水之源，我身得生成。 ";}
                    if (shi.equals("辛丑")){string+=" 丁火生 子月 辛丑 時 丁日子提，水旺火衰，時逢辛丑，金寒水冷之象，仲冬丁火微弱，最怕歸旺得垣之水，為之浸淫，際此水旺火熄之時，非有得地之厚土，不能挽狂瀾於旣倒，尤喜印比雙透，方可轉達情和。 ";}
                    if (shi.equals("壬寅")){string+=" 丁火生 子月 壬寅 時 丁日子提，水旺火弱，時干一壬，坐下月提子刃，官星之氣愈純，惟以火不勝水，官清未能為用，喜其時支寅木，寅中甲祿丙生，不惟有全日元之生，抑可以泄旺水之氣，冬丁以寅為嫡母，見之最為親切，苟有巳戌等字再來，壬官又何害於我乎。 ";}
                    if (shi.equals("癸卯")){string+=" 丁火生 子月 癸卯 時 丁火見印於時卯，時癸得祿於提子，殺以透干為旺，遇卯則泄之於印，惟以卯木見水則濕，不足以引生衰丁，須有得地之寅，透干之丙，則寒水之氣漸消，而木火自得鞏固矣。";}
                    if (shi.equals("甲辰")){string+=" 丁火生 子月 甲辰 時 子辰半會，濕辰轉化為殺，冬丁困於旺水，雖時透甲印，以火根淺薄，生火之力不足，故喜刧比多見，培養火之元氣，再來庚財相濟，則成用庚劈甲引丁之局矣，如有壬癸透干，土之食傷不可少。";}
                    if (shi.equals("乙巳")){string+="丁火生 子月 乙巳 時 丁生子提，寒火見水不發，時元印刧同來，丁力弱轉為强，月提之子，遇火而暖，冬丁以水旺為病，病其寒也，柱固有火，卽用之水殺，亦未始不可，如壬癸頻透天干，則非有土制，不能化權。 ";}
                    if (shi.equals("丙午")){string+=" 丁火生 子月 丙午 時 子午一冲，旺起而衰拔，日元丁火，賴午火為根，冲則根傾，八字時干丙火刼財，助丁有莫大之功，乃以午支不穩，仍作身弱看，須有寅巳等字見於地支方實，然後再見庚壬財官，亦非所忌矣。";}
                    if (shi.equals("丁未")){string+="丁火生 子月 丁未 時 丁火通根於未，時干丁比幫身，身主弱而不弱，月提子水，與時支未土，不無食殺交戰之象，仲冬丁火，每以水神為病，有土為藥，不知水透宜有土制，水藏何須土制，得有丙甲庚壬相濟尤妙。 ";}
                    if (shi.equals("戊申")){string+="  丁火生 子月 戊申 時 申子氣歸北方，日元丁火，臨於尅地也，時干戊土，雖有制水之功，而坐下申位，反來增財之勢，財旺轉來生官，是制之未能也，柱以尅泄兩見為病，印比重見方佳。";}
                    if (shi.equals("已酉")){string+="丁火生 子月 已酉 時 月提子水，尅制丁火，時下酉金，有戀水之情，而時干已土，不惟不能制水，抑且助金而生水，土金水順序相生，其重心固在於水，以孤立之丁，不將有損滅之虞乎，如干支印比皆絶，我當順其金水之情，而以從殺格論之。 ";}
                    if (shi.equals("庚戌")){string+=" 丁火生 子月 庚戌 時 丁火時戌，日元僅可言得根，火氣仍弱，月提子水，憚時戌之制，是戌土誠護火之神也，無如天干透庚，未能盡財之用，反有損丁之咎，甲係丁之嫡母，巳寅為護身之寶，柱中豈可無之耶。";}
                    if (shi.equals("辛亥")){string+="丁火生 子月 辛亥 時 亥子氣全北方，時干辛財生殺，水勢偏於一方，寒火氣怯而弱，大喜刧比叠見，八字復有甲印透干，以全日元精神，書云，冬丁有甲，不怕水多，為其印能化殺也，不怕金多，庚金劈甲，為其功成反生也。 ";}
               }
